import React, { useState } from "react";
import "../css/main.css";

const Main = () => {
  const [userEntry, setUserEntry] = useState("");
  const [scoreCount, setScoreCount] = useState(0);
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [currentLevelIndex, setCurrentLevelIndex] = useState(0);

  let wordList = [
    "Bonjour",
    "Mérite",
    "Cri",
    "Désespoir",
    "Soucis",
    "Corriger",
    "Araignée",
    "Chaton",
    "Bibliothèque",
    "Cascade",
    "Labyrinthe",
    "Radiateur",
    "Papillon",
    "Croissant",
    "Péripétie",
    "Chaussette",
    "Astronaute",
    "Harmonica",
    "Tempête",
    "Parapluie",
    "Marmelade",
    "Confortable",
    "Imagination",
    "Symphonie",
    "Liberté",
    "Abondance",
    "Exquis",
    "Vibrant",
    "Merveilleux",
    "Paradis",
    "Insouciance",
    "Douceur",
    "Fraternité",
    "Harmonieusement",
    "Rayonnement",
    "Essence",
    "Générosité",
    "Mélodie",
    "Antique",
    "Folie",
  ];

  const handleValidateClick = () => {
    validateUserEntry();
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      validateUserEntry();
    }
  };

  const validateUserEntry = () => {
    if (userEntry === wordList[currentWordIndex]) {
      setScoreCount(scoreCount + 1);
      setCurrentWordIndex(currentWordIndex + 1);
      setUserEntry("");
      animatePointsText();
      if (currentWordIndex === 20) {
        setCurrentLevelIndex(currentLevelIndex + 1);
      }
    }
  };

  const animatePointsText = () => {
    if (currentWordIndex++) {
      let pointsText = document.getElementById("pointsText");
      pointsText.style.opacity = 1;
      setTimeout(() => {
        pointsText.style.opacity = 0;
      }, 3000);
    }
  };

  return (
    <div id="mainBigFlexContainer">
      <div id="mainFlexContainer">
        <p id="mainTextDescription">
          Choisissez votre option et tapez la proposition qui s'affiche dans le
          champ en dessous.
        </p>
        <div id="radioContainer">
          <input type="radio" />
          <p className="mainTextRadio">Mots</p>
          <input type="radio" />
          <p className="mainTextRadio">Phrases</p>
        </div>
        <div id="mainWordContainer">
          <p id="mainWordText">{wordList[currentWordIndex]}</p>
        </div>
        <div id="mainProgressComponents">
          <input
            type="text"
            id="mainProgressContainer"
            value={userEntry}
            onChange={(e) => setUserEntry(e.target.value)}
            onKeyPress={handleKeyPress}
          ></input>
          <button id="mainValidateButton" onClick={handleValidateClick}>
            Valider
          </button>
        </div>
        <div id="mainScoreCount">
          <p id="mainScoreText">
            Votre score : {scoreCount} Niveau : {currentLevelIndex}{" "}
          </p>
        </div>
        <button id="SendScoreButton">Partager</button>
      </div>
      <div id="absoluteComponentsContainer">
        <p id="pointsText">+1</p>
      </div>
    </div>
  );
};

export default Main;
