import React from "react";
import "../css/main.css";

let userEntry = document.getElementById("mainWordText");
let scoreCount = 0;

let word = [
  "Bonjour",
  "Mérite",
  "Cri",
  "Désespoir",
  "Soucis",
  "Corriger",
  "Araignée",
];

let level = [0, 1, 2, 3, 4, 5, 6, 7];

function PickWord() {
  userEntry.addEventListener("change", () => {
    if (userEntry.textContent === word[0]) {
      scoreCount++;
    }
  });
}

const Main = () => {
  return (
    <div id="mainBigFlexContainer">
      <div id="mainFlexContainer">
        <p id="mainTextDescription">
          Choisissez votre option et tapez la proposition qui s'affiche dans le
          champ en dessous.
        </p>
        <div id="radioContainer">
          <input type="radio" />
          <p className="mainTextRadio">Mots</p>
          <input type="radio" />
          <p className="mainTextRadio">Phrases</p>
        </div>
        <div id="mainWordContainer">
          <p id="mainWordText">{word[0]}</p>
        </div>
        <div id="mainProgressComponents">
          <input type="text" id="mainProgressContainer"></input>
          <button id="mainValidateButton">Valider</button>
        </div>
        <div id="mainScoreCount">
          <p id="mainScoreText">Votre score : {scoreCount} </p>
        </div>
        <button id="SendScoreButton">Partager</button>
      </div>
    </div>
  );
};

export default Main;
